(4099, 1, '2016-03-28 16:26:15', '2016-03-28 07:26:15', '<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"> <img class="alignnone size-full wp-image-4098" src="http://dhlkorea.com/newsletter/wp-content/uploads/2014/05/2016봄호-사보퀴즈.png" alt="2016봄호 사보퀴즈" width="740" height="1150" /></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>\r\n<pre style="text-align: center;"></pre>', '[이벤트] 2016 봄호 사보퀴즈 이벤트', '', 'inherit', 'closed', 'closed', '', '459-revision-v1', '', '', '2016-03-28 16:26:15', '2016-03-28 07:26:15', '', 459, 'http://dhlkorea.com/newsletter/459-revision-v1/', 0, 'revision', '', 0),
